<template>
    <header>
        本层所见击杀数：<b>{{ killCount }}</b>
    </header>
    <main>
        <section v-if="currentEnemy">
            <p>
                <span class="type" :class="currentEnemy.Threat"
                    >[{{ currentEnemy.Threat }}]
                </span>
                <span class="title">{{ currentEnemy.Name }}</span>
            </p>
            <p>
                <b>{{ currentEnemy.Aggro }}</b
                >{{ currentEnemy.Aggro === 'Boss' ? '' : '感知' }}
            </p>
            <ul class="Weakness">
                <li
                    class="icon stun"
                    :class="{ valid: currentEnemy.Weakness.Stun }"
                ></li>
                <li
                    class="icon heavy"
                    :class="{ valid: currentEnemy.Weakness.Heavy }"
                ></li>
                <li
                    class="icon bind"
                    :class="{ valid: currentEnemy.Weakness.Bind }"
                ></li>
                <li
                    class="icon sleep"
                    :class="{ valid: currentEnemy.Weakness.Sleep }"
                ></li>
            </ul>
            <p class="desc">{{ currentEnemy.tip || '' }}</p>
        </section>
        <section v-else>
            <p class="desc floor">{{ currentFloorList[floorIndex] || '' }}</p>
        </section>
    </main>
</template>
<script setup>
import { ref, computed } from 'vue'
import init from './modules/init'
import { GetZoneDataById } from './modules/data/zone'

let currentIdMap = {}
let currentFloorList = []

const killCount = ref(0)
const floorIndex = ref(-1)
const currentId = ref(0)

const currentEnemy = computed(() => currentIdMap[currentId.value])

init({
    onLogEvent(type, id) {
        if (type === 'Death') {
            killCount.value++
        }
        if (type === 'Change') {
            currentId.value = id
        }
    },
    async ChangeZone(zoneId) {
        killCount.value = 0
        floorIndex.value = -1
        //决定使用哪套数据
        const { getEnemyData, getFloorData } = GetZoneDataById(zoneId)
        if (getEnemyData) {
            const { default: data } = await getEnemyData()
            currentIdMap = data
        }
        if (getFloorData) {
            const { default: data } = await getFloorData()
            currentFloorList = data
            // floorIndex.value = 0
        }
    },
})
</script>
